,p_process_name=>'Pre-Insert'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'if :P20_LOAN_ID is null then',
'    select nvl(max(LOAN_ID),0)+1 into :P20_LOAN_ID from LOAN;',
'    :P20_CREATED_BY:=:app_user;',
'    :P20_CREATED_ON:=sysdate;     ',
'    :P20_LOAN_STATUS:=''Open'';',
'    :P9_LOAN_BALANCE:=:P9_TOTAL_AMOUNT;',
'------------------- auto entries -------------------',
'            ---------transaction first entry------------',
'            INSERT INTO TRANSACTION',
'              (LOAN_ID, TX_DATE, TX_ID, TX_DESC, DEBIT, CREDIT, ',
'              CREATED_BY, CREATED_ON, UPDATED_BY, UPDATED_ON)',
'            select  :P20_LOAN_ID, :P20_FUND_RELEASE_DATE, nvl(max(TX_ID),0)+1, ''Loan drawndown - Loan ID''||:P20_LOAN_ID, :P20_LOAN_AMOUNT, 0, ',
'              :app_user, sysdate, :app_user, sysdate',
'             from TRANSACTION;',
'            ---------transaction second entry------------',
'            INSERT INTO TRANSACTION',
'              (LOAN_ID, TX_DATE, TX_ID, TX_DESC, DEBIT, CREDIT, ',
'              CREATED_BY, CREATED_ON, UPDATED_BY, UPDATED_ON)',
'            select  :P20_LOAN_ID, :P20_FUND_RELEASE_DATE, nvl(max(TX_ID),0)+1, ''Fees & Charges - Loan ID''||:P20_LOAN_ID, :P20_LOAN_FEES, 0, ',
'              :app_user, sysdate, :app_user, sysdate',
'             from TRANSACTION;    ',
'            ---------commission entry------------',
'            if :P20_AGENT_ID is not null then',
'                INSERT INTO COMMISSION',
'                  (AGENT_ID, TX_DATE, TX_ID, TX_DESC, DEBIT, CREDIT, ',
'                  CREATED_BY, CREATED_ON, UPDATED_BY, UPDATED_ON)',
'                select  :P20_AGENT_ID, :P20_FUND_RELEASE_DATE, nvl(max(TX_ID),0)+1, ''Fees & Charges - Loan ID''||:P20_LOAN_ID, nvl(a.referral_fees,0), 0, ',
'                  :app_user, sysdate, :app_user, sysdate',
'                 from COMMISSION t, AGENT a',
'                 where a.agent_id = nvl(:P20_AGENT_ID,a.agent_id)',
'                 group by a.agent_id, a.referral_fees;',
'             else',
'                INSERT INTO COMMISSION',
'                  (AGENT_ID, TX_DATE, TX_ID, TX_DESC, DEBIT, CREDIT, ',
'                  CREATED_BY, CREATED_ON, UPDATED_BY, UPDATED_ON)',
