'                  :app_user, sysdate, :app_user, sysdate',
'                 from COMMISSION t;             ',
'             end if;      ',
'',
'else',
'    :P20_UPDATED_BY:=:app_user;',
'    :P20_UPDATED_ON:=sysdate;   ',
'end if;',
''))
