'DECLARE',
'   -- Variables for Google API OAuth',
'   l_access_token   VARCHAR2(4000);',
'   l_file_name      VARCHAR2(100);',
'   l_file_blob      BLOB;',
'   l_file_size      NUMBER;',
'   l_boundary       VARCHAR2(100) := ''foo_bar_baz'';',
'   l_body           VARCHAR2(32767);',
'   l_http_req       UTL_HTTP.req;',
'   l_http_resp      UTL_HTTP.resp;',
'   l_url            VARCHAR2(4000);',
'   l_response       VARCHAR2(32767);',
'BEGIN',
'   -- Retrieve the access token (Assuming you''ve set up OAuth authentication in APEX)',
'   l_access_token := ''<AIzaSyCxAueWxmJaEXb_Mm3tKAzVNlc-uVuQGSc>'';',
'',
'   -- Get the uploaded file',
'   SELECT blob_content, filename, dbms_lob.getlength(blob_content)',
'   INTO l_file_blob, l_file_name, l_file_size',
'   FROM apex_application_temp_files',
'   WHERE name = :P1_FILE_UPLOAD;',
'',
'   -- Prepare HTTP request',
'   l_url := ''https://www.googleapis.com/upload/drive/v3/files?uploadType=multipart'';',
'   l_http_req := UTL_HTTP.begin_request(l_url, ''POST'', ''HTTP/1.1'');',
'   UTL_HTTP.set_header(l_http_req, ''Authorization'', ''Bearer '' || l_access_token);',
'   UTL_HTTP.set_header(l_http_req, ''Content-Type'', ''multipart/related; boundary='' || l_boundary);',
'   ',
'   -- Create request body for metadata and file content',
'   l_body := ''--'' || l_boundary || CHR(13) || CHR(10) ||',
'             ''Content-Type: application/json; charset=UTF-8'' || CHR(13) || CHR(10) || CHR(13) || CHR(10) ||',
'             ''{"name": "'' || l_file_name || ''"}'' || CHR(13) || CHR(10) || CHR(13) || CHR(10) ||',
'             ''--'' || l_boundary || CHR(13) || CHR(10) ||',
'             ''Content-Type: application/octet-stream'' || CHR(13) || CHR(10) || CHR(13) || CHR(10);',
'   ',
'   UTL_HTTP.write_text(l_http_req, l_body);',
'   ',
'   -- Write the binary content of the file',
'   UTL_HTTP.write_raw(l_http_req, l_file_blob);',
'   ',
'   -- Close the multipart request',
'   UTL_HTTP.write_text(l_http_req, CHR(13) || CHR(10) || ''--'' || l_boundary || ''--'');',
'   ',
'   -- Make the HTTP request',
'   l_http_resp := UTL_HTTP.get_response(l_http_req);',
'',
'   -- Capture the response',
'   UTL_HTTP.read_text(l_http_resp, l_response);',
'   UTL_HTTP.end_response(l_http_resp);',
'',
'   -- Log the response (optional)',
'   --apex_debug.log(''Google Drive API response: '' || l_response);',
'',
'EXCEPTION',
'   WHEN OTHERS THEN',
'      -- Handle errors',
'      apex_error.add_error (',
'         p_message   => ''File upload to Google Drive failed: '' || SQLERRM,',
'         p_display_location => apex_error.c_inline_in_notification',
'      );',
'END;',
''))
,p_process_clob_language=>'PLSQL'
